mapper.select:
SELECT

FROM USER_RUNNING_HISTORY
WHERE USER_ID = #{userId}

AND MISSION_ID = #{missionId}

SELECT

FROM USER_RUNNING_HISTORY
WHERE USER_ID = #{userId}
AND COMMIT_TIME > #{lastUpdateTime}
ORDER BY MISSION_DATE DESC
LIMIT #{startSize}, #{pageSize}

SELECT

FROM USER_RUNNING
WHERE USER_ID = #{userId}
AND COMMIT_TIME > #{lastUpdateTime}
